import { useState } from "react";
import axios from 'axios';

import { Div } from "./styles"

export default function Form(){
    const [sentence, setSentence] = useState('');
    const [result, setResult] = useState('');
    const [loading, setLoading] = useState(false);

    function handleButton(){
        const response_sentence = sentence;
        set
        setLoading(true);

        axios.post('http://localhost:5000/post_sentence', {sentence})
            .then(response => {
                setResult(response.data.sentence)
                setLoading(false);
            })
            .catch(error => {
                console.log('Erro ao enviar a frase: ', error)
            });
    }

    return (
        <Div>
            <textarea 
                placeholder="Insert a Sentence"
                name="set_sentence"
                value={sentence}
                onChange={(e) => setSentence(e.target.value)}
            />
            <select>
                <option>English</option>
                <option>Portuguese</option>
                <option>Spanish</option>
                <option>Mandarin</option>
            </select>
            <button onClick={handleButton}>Check</button>
            <div>
                <span>{ loading ? 'Carregando...' : result }</span>
            </div>
        </Div>
    )
}
