import { styled } from 'styled-components';

export const Para = styled.p`
    .POSITIVE{
        color: green;
    }

    .NEGATIVE{
        color: red;
    }

    .result span{
        font-size: 20px;
        font-weight: bold;
    }

    .language{
        color: #3498db;
    }
`;
