import { styled } from 'styled-components';

export const Container = styled.header`
    h1{
        margin: 30px 0;
        font-weight: lighter;
        letter-spacing: 3px;
    }
    
    p{
        line-height: 30px;
        font-size: 18px;
    }
`;
