import { styled } from 'styled-components';

export const Container = styled.main`
    border-radius: 10px;
    box-shadow: 10px 0 30px -10px rgba(0, 0, 0, 0.2), -10px 0 30px -10px rgba(0, 0, 0, 0.2);
    margin: 80px auto 0 auto;
    text-align: center;
    width: 45vw;
    padding: 20px;
`;
