import styled, { css } from 'styled-components';

export const Container = styled.span`
    ${({ values }) => {
        if (values.classSpan === 'spanError') {
            return css`
                color: red;
            `;
        } else if (values.classSpan === 'spanWarning') {
            return css`
                color: orange;
            `;
        }
    }}
`;
