import { useState } from "react";
import axios from 'axios';

import { LoadingContainer, LoadingCircle } from "./stylesLoading";

import { Div } from "./styles"

export default function Form(){
    const [sentence, setSentence] = useState('');
    const [sentiment, setSentiment] = useState('');
    const [language, setLanguage] = useState('');
    const [loading, setLoading] = useState(false);
    const [displayResult, setDisplayResult] = useState('none');

    function handleButton(){
        const response_sentence = sentence;

        setSentence('');
        setLoading(true);

        axios.post('http://localhost:5000/post_sentence', {response_sentence})
            .then(response => {

                setSentiment(response.data.sentiment)
                setLanguage(response.data.sentence_language)

                setLoading(false);
                setDisplayResult('block')
            })
            .catch(error => {
                console.log('Erro ao enviar a frase: ', error)
            });
    }

    return (
        <Div>
            <textarea 
                placeholder="Insert a Sentence"
                name="set_sentence"
                value={sentence}
                onChange={(e) => setSentence(e.target.value)}
            />
            <button onClick={handleButton}>Check</button>
            <div className="result"  style={{display: "none"}}>
                {loading ? (
                    <LoadingContainer>
                        <LoadingCircle />
                        <LoadingCircle />
                        <LoadingCircle />
                    </LoadingContainer>
                ) : (
                    <p>
                        The digited sentence was scripted in <span>{ language.toUpperCase() }</span> and it was classified as <span className={ sentiment }>{ sentiment }</span>
                    </p>
                )}
            </div>
        </Div>
    )
}
