import { styled } from 'styled-components';

export const Div = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px;
    padding: 20px 0;

    textarea{
        resize: none;
        height: 50px;
    }

    textarea, select, button{
        padding: 5px;
        border-radius: 10px;
    }

    button{
        border: 1px solid;
    }
`;
